import requests, time
from selenium import webdriver
from selenium.webdriver.chrome.service import Service
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as es

url="https://finance.yahoo.com/quote/AAPL/chart?p=AAPL#"

service = Service("./chromedriver.exe")
options = webdriver.ChromeOptions()
options.add_argument("headless")
driver = webdriver.Chrome(service=service, options=options) #driver = webdriver.Chrome(executable_path="./chromedriver")
driver.get(url)

#Method1 Static Wait
#This is to wait for up to 10 seconds till website is loaded properly
#driver.implicitly_wait(10)

#Method2 Dynamic Wait
loadtime = 10
wait = WebDriverWait(driver, loadtime)


xPath='//*[@id="recommendations-by-symbol"]/table/tbody/tr[1]'
try:
    #Part of Method2 to wait for loading time
    wait.until(es.visibility_of_element_located((By.XPATH, xPath)))
    elem = driver.find_elements(By.XPATH, xPath)
    print(elem[0].text.split("\n"))
except Exception as e:
    print(str(e))
    pass

